{"source":"var skuPrices = [];
var skuPricesStr;
var items = digitalData.transaction.item;
var itemPrices;
var itemSkus = digitalData.transaction.attributes.skuIds;
var firstOnlineTransaction = digitalData.transaction.attributes.firstTransactionOnline;
var GIFT_WRAP_SKUS = ['1169945', '1169937'];
var PLAY_SKU_ID = '1001';

var safelyConvertToUSD = function(price) {
    /* Remove any currency from the front or back of the string */
    price = price.match(/\d+(?:\.\d+|\,\d+\.\d+|\,\d+)|\d+/);

    if (price && price.length) {
        price = price[0];

         /* Remove any commas because they break the convertToUSD method */
        /* Adding decimals in place of commas for CA/FR */
        if (digitalData.page.pageInfo.language.toLowerCase() === 'fr') {
            price = price.replace(',', '.');
        } else {
            price = price.replace(',', '');
        }
    }

    /* Convert to USD using Signal Library */
    price = Sephora.analytics.utils.convertToUSD(price); 
    return price;
};

items.map( 
  function(item, index) {
    var id = itemSkus[index];
    var price = item.sku.salePrice ? item.sku.salePrice : item.sku.listPrice;
    var isReward = (price.toLowerCase().indexOf('points') !=- 1);
    var isGiftWrapSku = GIFT_WRAP_SKUS.indexOf(id) > -1;

    // Do not include gift wrap skus
    if (isGiftWrapSku) {
      return; 
    }

    // Hard code price for play subscription per comments in https://jira.sephora.com/browse/ILLUPH-63077
    if (id === PLAY_SKU_ID){
      //Play Sku Price is $10.00
      skuPrices.push('10.00');
    } else {
        if (isReward || price === 'FREE') {
            skuPrices.push('0.00');
        } else {
            skuPrices.push(safelyConvertToUSD(price));
        }
    }
  } 
); 

if (firstOnlineTransaction) {
   skuPricesStr = skuPrices.join('|')+'|0';
} else {
 skuPricesStr = skuPrices.join('|');
}
return skuPricesStr; "}
