{"source":"var productAvailable = false;
if (digitalData.product && Array.isArray(digitalData.product) && digitalData.product.length > 0) {
  productAvailable = true;
}

function FormatPrice(listPrice) {
	let price ='0.00';
	if (listPrice !== 'FREE' && listPrice !== 'GRATUIT') {
		price = listPrice;
		price = price.replace(/\s+/g, "");
		price = price.match(/\d+(?:\.\d+|\,\d+\.\d+|\,\d+)|\d+/)[0];
		if (digitalData.page.pageInfo.language.toLowerCase() === 'fr') { 
			price = price.replace(',', '.'); 
		} 
		else { 
			price = price.replace(',', ''); 
		}
	}
	return price;
}

var productPrice = productAvailable ? digitalData.product[0].attributes.price : '0';
var eventPayloadPrice = event === undefined ? "" : productPrice || '0'; 
var price = Number(FormatPrice(eventPayloadPrice));



var reddit_skuQuantity = Number(event === undefined ? "" : event.detail.data.analyticsData.qty) || 1;

var eventID = "addtocart-" + _satellite.getVar('[UEID] :: Universal Event ID Fx')(event);

_satellite.getVar('[TMS] Debug Log For QA Envs Fx')(`Reddit Event Debugger: ${eventID}`);

// Gets the Product Name and Brand
var productName = `${event.detail.data.analyticsData?.sku?.productName || ''}`;
var brand = `${event.detail.data.analyticsData?.sku?.brandName || ''}`;

var productNameAndBrand = brand !== '' ? `${productName} | ${brand}` : productName;

var products = [{
  "id": event === undefined ? '' : event.detail.data.analyticsData?.sku?.skuId || '',
  "name": productNameAndBrand,
  "category": event === undefined ? '' : event.detail.data.analyticsData?.category || ''
}];

rdt('track', 'AddToCart', {
  "currency": "USD",
  "itemCount": reddit_skuQuantity,
  "value": price * reddit_skuQuantity,
  "products": products,
  "conversionId": eventID
});","language":"javascript"}
