{"source":"const REGULAR_TYPE = 'Standard';
const PLAY_SKU_ID = '1001';
const GIFT_WRAP_SKUS = ['1169945', '1169937'];
const IS_PLAY_ORDER = digitalData.transaction.attributes.isPlayOrder;
const universalEventId = _satellite.getVar('[UEID] :: Universal Event ID Fx')(event);
const eventId = `purchase-${universalEventId}`;
const cost = _satellite.getVar('[TMS] Pixel :: Pinterest :: Order Subtotal');

var safelyConvertToUSD = function(price) {
    /* Remove any currency from the front or back of the string */
    price = price.match(/\d+(?:\.\d+|\,\d+\.\d+|\,\d+)|\d+/);

    if (price && price.length) {
        price = price[0];

        /* Remove any commas because they break the convertToUSD method */
        /* Adding decimals in place of commas for CA/FR */
        if (digitalData.page.pageInfo.language.toLowerCase() === 'fr') {
            price = price.replace(',', '.');
        } else {
            price = price.replace(',', '');
        }
    }

    /* Convert to USD using UFE's Library */
    price = Sephora.analytics.utils.convertToUSD(price);
    return price;
};

var itemsToAdd = [];

var sumQty = 0;
var hasRegularProduct = false;

var _items = digitalData?.transaction?.item || [];

_items.forEach((item) => {
	const itemSku = item?.sku?.skuId || '';
	var itemPrice = (item?.sku?.salePrice || item?.sku?.listPrice) || 0;
	const isReward = (itemPrice.toLowerCase().indexOf('points') != -1);
	const itemSkyType = item?.sku?.type || '';
	const itemQty =item?.qty || 0;

	const isGiftWrapSku = GIFT_WRAP_SKUS.indexOf(itemSku) > -1;

	const price = (itemSku === PLAY_SKU_ID) || (isReward || itemPrice === 'FREE') ? '0' : itemPrice;
	itemPrice = safelyConvertToUSD(price);

	if (!isGiftWrapSku && Number(itemPrice) !== 0 && itemSkyType === REGULAR_TYPE) {
		hasRegularProduct = true;
		sumQty += itemQty;

		itemsToAdd.push({
			product_id: item?.sku?.skuId || '',
			product_name: item?.sku?.productName || '',
			product_quantity: itemQty,
			product_price: itemPrice
		});
	}
});

var lineItems = itemsToAdd.filter(item => item.product_price !== '0');

function FirePinterestPageVisitPixel() {
  pintrk('track', 'checkout', {
      value: cost,
      order_quantity: sumQty,
      currency: 'USD',
      order_id: _satellite.getVar('[TMS] Page :: Order Number'),
      line_items: lineItems,
      event_id: eventId
  });
}

if (!IS_PLAY_ORDER && hasRegularProduct) {
  Sephora.analytics.promises.PinterestBasePixelInitialized.then(function(){ FirePinterestPageVisitPixel(); });
}

","language":"javascript"}
