{"source":"function fireSnapchatAddToCartTag() {
  let isRestrictedForPixel = _satellite.getVar('[TMS] IsRestrictedForPixel')(event);

  // AddToCart PIXELS should not fire for Gift Card, Samples, GWP & Rewards
  if (!isRestrictedForPixel) {
    // Extracts the list price, removes the currency symbols and converts to USD when applicable.
    var listPrice = event.detail.data.sku.listPrice;
    listPrice = Sephora.analytics.utils.removeCurrencySymbol(listPrice);
    var price = Sephora.analytics.utils.convertToUSD(listPrice); 

    var number_items = event.detail.data.analyticsData.qty;
    var currency = event.detail.data.analyticsData.currency;
    var item_ids = event.detail.data.analyticsData.sku.skuId;
    var client_dedup_id = `addtocart-${_satellite.getVar("[UEID] :: Universal Event ID Fx")(event)}`;
    var itemCategory = event.detail.data.analyticsData.category;
    var itemBrand = event.detail.data.analyticsData.brandName;

    /*
    if (digitalData.product && Array.isArray(digitalData.product) && digitalData.product.length > 0) {
      itemCategory = digitalData.product[0].attributes.nthLevelCategory || '';
      itemBrand = digitalData.product[0].productInfo.manufacturer || '';
    }
    */

    snaptr('track', 'ADD_CART', {
      'price': price,
      'number_items': number_items,
      'currency': currency,
      'item_ids': item_ids,
      'client_dedup_id': client_dedup_id,
      'item_category': [itemCategory],
      'brands': [itemBrand]
    });
  }
}

if (snaptr) {
  fireSnapchatAddToCartTag();
}
else {
  Sephora.analytics.promises.snapChatReady.then(() => fireSnapchatAddToCartTag());
}","language":"javascript"}
