{"source":"function AdvancedMatching(fireTagFx) {
  // Advance Matching
  var dd_user = digitalData.user ? digitalData.user[0] : null;
  var dd_profile = dd_user ? (dd_user.profile ? dd_user.profile[0] : null) : null;
  var dd_profileInfo = dd_profile ? dd_profile.profileInfo : null;
  var dd_hashedEmail = dd_profileInfo ? dd_profileInfo.email : null; // webAnalyticsHash
  var dd_profileId = dd_profileInfo ? dd_profileInfo.profileID || '' : null;

  var userInfoCache = Sephora.Util.getUserInfoCache();
  var phoneNumber = userInfoCache ? userInfoCache.profile.phoneNumber : null;

  Sephora.analytics.utils.hashString(dd_profileId).then(hashedProfileID => {
    ttq.identify({
      email: dd_hashedEmail, 
      // phone_number: phoneNumber,
      external_id: hashedProfileID,
    });

    fireTagFx();
  });
}

function FireTag_tiktok_PurchaseEvent() {
  var universalEventId = _satellite.getVar('[UEID] :: Universal Event ID Fx')(event);
  var eventId = `purchase${universalEventId}`;
  var digitalDataTimestamp = digitalData.page.attributes.date.timestamp;
  var brand = digitalData.transaction.attributes.brandNames;
  var categories = digitalData.transaction.attributes.categories || [];
  var productNames = digitalData.transaction.attributes.itemName || [];
  var itemShorts = digitalData.transaction.itemShort;
  var value = Number(digitalData.transaction.total.transactionSubtotal);
  var content = [];



   for (var i = 0; i < itemShorts.length; i++) {
      var itemShort = itemShorts[i];

      var products = {
          'content_id': itemShort.id,
          'quantity': itemShort.quantity,
          'content_name': productNames[i],
          'content_type': 'product',
          'price': Number(itemShort.price),
          'content_category': categories[i],
          'brand': brand[i]
      };
      content.push(products);
  }

  ttq.instance(_satellite.getVar('[TMS] TikTok_AccountID')).track('CompletePayment', 
  { 
  contents: content,
  value: value,   
  currency: digitalData.transaction.attributes.isoCurrency  
  },{event_id: eventId });
}

if (!ttq) {
  // Expects the Tiktok Pixel to be loaded
  window.addEventListener('TikTokBasePixelInitialized', AdvancedMatching(FireTag_tiktok_PurchaseEvent));
}
else {
  AdvancedMatching(FireTag_tiktok_PurchaseEvent);
}

