{"source":"var contents = [];
var skuIds = digitalData.transaction.attributes.sdd.skuIds;
var skuQty = digitalData.transaction.attributes.sdd.itemQty;
var biAccountId = Sephora.Util.getUserInfoCache()?.profile?.beautyInsiderAccount?.biAccountId || '';

var eventId = 'sdd-' + _satellite.getVar('[UEID] :: Universal Event ID Fx')(event);

if (skuIds.length) {
  for (var i = 0; i < skuIds.length; i++) {
    var item = {};
    item.id = skuIds[i];
    item.quantity = skuQty[i];
    contents.push(item);
  }
}

// AQME-4645: Discrepancy between AL and SOT. Tags should send CAD when using Canadian site
var currency = digitalData.page.pageInfo.country === 'US' ? 'USD' : 'CAD';

var transactionSubTotal = digitalData.transaction.total.sdd.transactionSubtotal || '0';
var cleanedTransactionSubTotal = transactionSubTotal.replace("$", "").replace(',', '.');
var subtotal = Number(cleanedTransactionSubTotal).toFixed(2); 
var value = subtotal || 0;
var discount = parseFloat(digitalData.transaction.total.promotionDiscount) || 0;
value = value - discount;

var data = {
  contents: contents,
  content_type: 'product',
  value: value,
  currency: currency,
  delivery_option: 'Same Day_delivery option',
  customer_type: _satellite.getVar('[TMS] Pixel :: Facebook :: BI Status'),
  content_local: _satellite.getVar('[TMS] Page :: Language/Locale'), 
  order_id: _satellite.getVar('[TMS] Page :: Order Number') 
};

window.addEventListener(
  'FacebookBasePixelInitialized',
  function () {
    if (biAccountId) {
      Sephora.analytics.utils.hashString(biAccountId).then(hashedBiId => {
        fbq('trackCustom', 'sameDayDelivery', { ...data, external_id: hashedBiId }, { eventID: eventId });
      });
    } else {
       fbq('trackCustom', 'sameDayDelivery', data, { eventID: eventId }); 
    }
  }
);","language":"javascript"}
