{"source":"if (optimoveSDK) {
  var eventData = event?.detail?.data || undefined;
  var profileId = digitalData?.user[0]?.profile[0]?.profileInfo?.profileID || '';
  var biAccountId = Sephora.Util.getUserInfoCache()?.profile?.beautyInsiderAccount?.biAccountId || '';
  var analyticsData = eventData?.analyticsData || {};

  const getLastNestedId = (obj, keyField, childKey) => {
    const id = obj[keyField];
    if (obj[childKey] === undefined) {
      return id;
    }
    return getLastNestedId(obj[childKey], keyField, childKey);
  };

  // Formats the Price and Converts to USD when applicable.
  var om_price = eventData?.sku?.listPrice || '';  
  om_price = om_price.replace(',', '.');
  om_price = om_price.replace('$', '');
  om_price = om_price.trim();
  om_price = Sephora.analytics.utils.convertToUSD(om_price);

  var om_qty = eventData?.sku?.qty || 1;
  var om_totalprice = om_price * om_qty;

  var om_skuId = analyticsData?.skuId || '';
  var om_productId = analyticsData?.productId || '';
  var om_url = analyticsData?.url || '';

  var om_category_id = eventData?.sku?.sku?.parentCategory?.categoryId || '';
  var om_category_name = eventData?.sku?.sku?.parentCategory?.displayName || '';

  // Key Identifying the department or highest level of category.
  var on_department_name = getLastNestedId(eventData?.sku?.sku?.parentCategory || {}, 'displayName', 'parentCategory');
  var on_department_id = getLastNestedId(eventData?.sku?.sku?.parentCategory || {}, 'categoryId', 'parentCategory');

  var parameters = {
    "userid": "",
    "biId": "",
    "eventdatetime": _satellite.getVar('[TMS] Event Datetime With Millisecond Format'),
    "product_id": om_productId,
    "url": om_url,
    "cartTotal": om_totalprice,
    "Source": "Optimove SDK",
    "fulfillment_type": "bopis",
    "category_id": om_category_id,
    "category_name": om_category_name,
    "department_id": on_department_id,
    "department_name": on_department_name
    // Place holder parameters.
    // "sku_id": om_skuId,
    // "cartTotal": om_totalprice
    // "currency": "USD"
  };

  Sephora.analytics.utils.hashString(profileId).then(hashedProfileId => {
    parameters = {
      ...parameters,
      "userid": hashedProfileId
    };
    Sephora.analytics.utils.hashString(biAccountId).then(hashedBiId => {
      parameters = {
        ...parameters,
        "biId": hashedBiId
      };

      optimoveSDK.API.reportEvent('ootb_bopis_add_to_cart', parameters);
    });
  });
}","language":"javascript"}
