{"source":"/* This should only happen after the base pixel has fired.
** We are using a modified arguments list to avoid multiple calls to the same pixel.
** Read more aboue invoking with 'trackSingle' here:
** https://developers.facebook.com/ads/blog/post/2017/11/28/event-tracking-with-multiple-pixels-tracksingle/
*/

var prodStr = digitalData.page.attributes.search.productsDisplayed;
var arrVal = [];
if (typeof prodStr === 'string') {
    arrVal = digitalData.page.attributes.search && prodStr ? prodStr.split(',') : [];
}
if (prodStr &&  prodStr instanceof Array) {  
    for(var i=0; i<prodStr.length; i++) {
     var productId = prodStr[i] && prodStr[i].currentSku && prodStr[i].currentSku.skuId;
     if (productId) { 
         arrVal.push(productId);
     }
    }
}

var eventId = "search-" + _satellite.getVar('[UEID] :: Universal Event ID Fx')(event);

var biStatus = digitalData.user[0].segment.fbBiStatus;
var biAccountId = Sephora.Util.getUserInfoCache()?.profile?.beautyInsiderAccount?.biAccountId || '';
var facebookPixelId = _satellite.getVar('[TMS] Facebook Pixel ID');

// Changes the search term from 'lip%balm' to 'lip balm'
var searchTerm = _satellite.getVar('[TMS] Page :: Search Term');
searchTerm = !searchTerm ? searchTerm : unescape(searchTerm);

var data = {
    Content_type: 'product',
    customer_type: biStatus,
    Search_string: searchTerm,
    Content_ids: arrVal,
    origin: 'Adobe Launch - Facebook_searchView'
};

if(data.Search_string) {
  if (biAccountId) {
    Sephora.analytics.utils.hashString(biAccountId).then(hashedBiId => {
        fbq('trackSingle', facebookPixelId, 'Search', { ...data, external_id: hashedBiId }, { eventID: eventId });
    });
  } else {
    fbq('trackSingle', facebookPixelId, 'Search', data, { eventID: eventId }); 
  }
}","language":"javascript"}
