{"source":"if (optimoveSDK) { 
  var eventData = event?.detail?.data;

  const getLastNestedId = (obj, keyField, childKey) => {
    const id = obj[keyField];
    if (obj[childKey] === undefined) {
      return id;
    }
    return getLastNestedId(obj[childKey], keyField, childKey);
  };

  // Key Identifying the department or highest level of category.
  // var on_department_name = getLastNestedId(eventData?.sku?.parentCategory || {}, 'displayName', 'parentCategory');
  // var on_department_id = getLastNestedId(eventData?.sku?.parentCategory || {}, 'categoryId', 'parentCategory');

  var parameters = {
    "userid": "",
    "biId": "",
    "eventdatetime": _satellite.getVar('[TMS] Event Datetime With Millisecond Format'),
    "cartTotal": eventData?.totalBasketCount || 0,
    "product_id": eventData?.sku?.productId || '',
    "url": eventData?.sku?.fullSiteProductUrl || '',
    "sku_id": eventData?.sku.skuId || '',
    "Source": "Optimove SDK",
    "brand": eventData?.sku?.brandName || '',
    "category_id": eventData?.sku?.parentCategory?.categoryId || '',
    "category_name": eventData?.sku?.parentCategory?.displayName || '',
    "department_id": eventData?.sku?.parentCategory?.categoryId || '',
    "department_name": eventData?.sku?.parentCategory?.displayName || ''
  };

  var profileId = digitalData?.user[0]?.profile[0]?.profileInfo?.profileID || '';
  var biAccountId = Sephora.Util.getUserInfoCache()?.profile?.beautyInsiderAccount?.biAccountId || '';

  Sephora.analytics.utils.hashString(profileId).then(hashedProfileId => {
    parameters = {
      ...parameters,
      "userid": hashedProfileId
    };
    Sephora.analytics.utils.hashString(biAccountId).then(hashedBiId => {
      parameters = {
        ...parameters,
        "biId": hashedBiId
      };

      optimoveSDK.API.reportEvent('ootb_remove_from_cart', parameters);
    });
  }); 
}","language":"javascript"}
