var pricesList = cartData.map(c => {
  // Formats the Price and Converts to USD when applicable.
  var cartItem = c?.listPriceSubtotal || '';  
  cartItem = cartItem.replace(',', '.');
  cartItem = cartItem.replace('$', '');
  cartItem = cartItem.trim();
  cartItem = Sephora.analytics.utils.convertToUSD(cartItem);

  // The basket shows the total without multiplying by the qty. This tag should refflect the same.
  return Number(cartItem || 0);
});

var cartTotal = pricesList.reduce((accumulatedPrice, currentPrice) =>  accumulatedPrice + currentPrice, 0);

