{"source":"var biAccountId = Sephora.Util.getUserInfoCache()?.profile?.beautyInsiderAccount?.biAccountId || '';
var brand = digitalData.product[0].productInfo.manufacturer || '';
var pageWorld = (digitalData.page.attributes.world || 'n/a').toLowerCase();
var price = 0;

// Verifies the product carries the price information.
if (digitalData.product[0].attributes.price !== undefined) {
    price = Number(digitalData.product[0].attributes.price.replace("$", "").replace(',', '.'));
}

// AQME-4645: Discrepancy between AL and SOT. Tags should send CAD when using Canadian site
var currency = digitalData.page.pageInfo.country === 'US' ? 'USD' : 'CAD';

var eventId = "viewcontent-" + _satellite.getVar('[UEID] :: Universal Event ID Fx')(event);

var data = {
  contents: [ 
    {
      content_ids: _satellite.getVar("[TMS] Pixel :: Facebook :: Stringified Sku Array - productPageLoad"),
      quantity: 1,
      brand : brand, 
      product_type : pageWorld,
      customer_type: _satellite.getVar("[TMS] Pixel :: Facebook :: BI Status"),
      content_local: _satellite.getVar("[TMS] Page :: Language/Locale")
    },
  ],
  content_type: 'product',
  value: price,
  currency: currency
}

if (biAccountId) {
  Sephora.analytics.utils.hashString(biAccountId).then(hashedBiId => {
  fbq('track', 'ViewContent', { ...data, external_id: hashedBiId }, { eventID: eventId });
  });
} else {
  fbq('track', 'ViewContent', data, { eventID: eventId });
}
","language":"javascript"}
