{"source":"function fireSnapChatquickLoadViewTag() {
  // Price for the selected SKU converted to USD for US sites.
  var cost = _satellite.getVar('[TMS] Product Price');
  var currencyCost = _satellite.getVar('[TMS] Double Click :: Product Page :: Complete Sku Price') || '0';
  var productType = _satellite.getVar('[TMS] Double Click :: Product Page :: Product Type');
  var originalPrice = digitalData.product[0].attributes.originalPrice;

  var eventId = `viewcontent-${_satellite.getVar('[UEID] :: Universal Event ID Fx')(event)}`;

  var viewedProduct = digitalData.product && Array.isArray(digitalData.product) && digitalData.product.length > 0 ? digitalData.product[0] : undefined;

  if (viewedProduct) {
    var pricesInCSV = cost || '';
    var productCategories = [viewedProduct.attributes.nthLevelCategory] || [];
    var itemSkusIds = viewedProduct.attributes.skuId || '';

    if (currencyCost.indexOf('Points') < 0 && productType === 'Standard' && cost > 0) { 
      snaptr('track', 'VIEW_CONTENT', {
          'price': pricesInCSV,
          'currency': 'USD',
          'item_ids':  itemSkusIds, 
          'client_dedup_id': eventId,
          'number_items': '1',
          'item_category': productCategories
      });
    }
  }
}

if (snaptr) {
  fireSnapChatquickLoadViewTag();
}
else {
  Sephora.analytics.promises.snapChatReady.then(() => fireSnapChatquickLoadViewTag());
}
","language":"javascript"}
