{"source":"    var gaId = _satellite.getVar('[TMS] UA Property Id');     
    var orderSubtotal = Number(digitalData.transaction.total.transactionSubtotal).toFixed(2); 
    var transactionValue = 0;
    if (orderSubtotal && orderSubtotal !== 'NaN') { 
        transactionValue = orderSubtotal; 
    }
    var biAccountNumber; 
    var profileId = digitalData.user[0].profile[0].profileInfo.profileID;
    if (digitalData.user[0].segment.biAccountId) {
        biAccountNumber = digitalData.user[0].segment.biAccountId;
    }
    var languageLocale = digitalData.page.attributes.languageLocale.toLowerCase();
    var biStatus = digitalData.user[0].segment.biStatus;
    var biPoints = digitalData.user[0].segment.biPoints;
    var loginStatus = _satellite.getVar('[TMS] Pixel :: User :: Login Status');
    var fivePIDs = digitalData.page.attributes.externalRecommendations.firstFivePIDs;
    var transactionCurrency = digitalData.transaction.attributes.isoCurrency;
    var transactionId = _satellite.getVar("[TMS] Page :: Order Number");
    var transactionItems = digitalData.transaction.attributes.skuIds;
    gtag('event', 'Reservation', {
        'send_to': gaId,
        'event_category': 'User Engagement',
        'event_label': 'ROPIS Reservation',
        'value': transactionValue,
        'profileId': profileId,
        'biAccountId': biAccountNumber,
        'biStatus': biStatus,
        'biPoints': biPoints,
        'firstFivePIDs': fivePIDs,
        'loginStatus': loginStatus,
        'userLocale': languageLocale.split('-')[0],
        'userLanguage': languageLocale.split('-')[1],
        'ropisValue': transactionValue,
        'ropisCurrency': transactionCurrency,
        'ropisOrderId': transactionId,
        'ropisProductSkus': transactionItems
    });
","language":"javascript"}
