{"source":"function getCategoriesForEvent(obj, categories, currentLevel) {
	categories.push({
		categoryId: obj.categoryId, 
		displayName: obj.displayName
	});
	if (!obj.parentCategory || currentLevel === 4) {
		return categories;
	}
	return getCategoriesForEvent(obj.parentCategory, categories, ++currentLevel);
}

let cartValue = 0;
const baskets = digitalData.cart.itemsByBasket;
let items = [];

baskets.map(b => {
  items = b.items.map(x => {     
    const itemsInBasket = x.items;
    const price = Number(typeof x.sku.listPrice === 'string' ? x.sku.listPrice.replace("$", "").replace(",", ".")  : 0);
    const categories = getCategoriesForEvent(x.sku.parentCategory, [], 0);
    let objToReturn = {
        quantity: x.qty, 
        item_id: x.sku.skuId, 
        item_name: x.sku.productName, 
        item_brand: x.sku.brandName,
        item_variant: x.sku.variationValue,
        price: price,
        index: 0,
        coupon: '', 
        discount: 0,
        location_id: '',
        affiliation: '',
        item_list_id: '',
        item_list_name: ''
    };

    cartValue += x.qty * price;

    let idx = 1;

    categories.forEach(c => {
        const idxLabel = idx === 1 ? '' : idx; 
        objToReturn['item_category' + idxLabel] = c.displayName;
        idx++;
    });

    return objToReturn; 
  });
});

