{"source":"var tid = digitalData.transaction.transactionID; 
var universalEventId = _satellite.getVar('[UEID] :: Universal Event ID Fx')(event);
var eventId = `${universalEventId}`;
var sddSkuIds = digitalData.transaction.attributes.sdd.skuIds;

function FormatPrice(item) {
	let price ='0.00';
	if (item.sku.listPrice !== 'FREE') {
		price = item.sku.listPrice;
		price = price.replace(/\s+/g, "");
		price = price.match(/\d+(?:\.\d+|\,\d+\.\d+|\,\d+)|\d+/)[0];
		if (digitalData.page.pageInfo.language.toLowerCase() === 'fr') { 
			price = price.replace(',', '.'); 
		} 
		else { 
			price = price.replace(',', ''); 
		}
		price = Sephora.analytics.utils.convertToUSD(price);
	}
	return price;
}

function GetFormattedItems(items) {
	const item_skus = items.map(p => p.sku.skuId) || [];
	const item_qty = items.map(p => p.qty).join(',') || '';
	const item_categories = items.map(p => p.sku.parentCategory.displayName) || [];
	const item_prices = items.map(p => FormatPrice(p)).join(',');
    const item_brands = items.map(p => p.sku.brandName) || [];
	return { item_skus, item_qty, item_categories, item_prices, item_brands }
}

function FireSnapchatPurchaseTag(data) {
  setTimeout(() => {
      snaptr('track', 'PURCHASE', data);
  }, 2000);
}

const stdItems = digitalData.transaction.item.filter(p => 
	p.sku.type === 'Standard' && 
	p.sku.listPrice.indexOf('Points') <= -1 &&
	sddSkuIds.indexOf(p.sku.skuId) == -1);

const sddItems = digitalData.transaction.item.filter(p => 
	sddSkuIds.indexOf(p.sku.skuId) >= 0);

if (stdItems.length > 0) {
	const { item_skus, item_qty, item_categories, item_prices, item_brands } = GetFormattedItems(stdItems);

	var dataToSendStandard = {
		'currency': 'USD',
		'price': item_prices,
		'transaction_id': tid,
		'number_items': item_qty,
		'client_dedup_id': 'purchase-' + eventId,
		'item_ids': item_skus,
		'item_category': item_categories,
        'brands': item_brands
	}

    if (snaptr) {
      FireSnapchatPurchaseTag(dataToSendStandard);
    }
    else {
      Sephora.analytics.promises.snapChatReady.then(() => FireSnapchatPurchaseTag(dataToSendStandard));
    }
}

if (sddSkuIds && sddSkuIds.length > 0) {
  const { item_skus, item_qty, item_categories, item_prices, item_brands } = GetFormattedItems(sddItems);

  var sddDataToSend = {
    'currency': 'USD',
    'price': item_prices,
    'transaction_id': tid,
    'number_items': item_qty,
    'client_dedup_id': 'sdd-' + eventId,
    'item_ids': item_skus,
    'item_category': item_categories,
    'event_tag': 'same_day_delivery',
    'brands': item_brands
  }

  if (snaptr) {
    FireSnapchatPurchaseTag(sddDataToSend);
  }
  else {
    Sephora.analytics.promises.snapChatReady.then(() => FireSnapchatPurchaseTag(sddDataToSend));
  }
}


","language":"javascript"}
