operations.body:
query CustomerLimitQuery($request: GetCustomerLimitInput!) {
  customerLimit(request: $request) {
    currency
    balance {
      initial
      initialExpiryDate
      totalSpend
      available
      __typename
    }
    __typename
  }
}

query Recommendations($request: ProductRecommendationInput!) {
  recommendations(request: $request) {
    resultId
    response {
      pod {
        id
        displayName
        __typename
      }
      results {
        value
        strategy {
          id
          __typename
        }
        data {
          productId
          brandName
          url
          imageUrl
          rating
          totalReviews
          currentSku {
            colorName
            biExclusivityLevel
            isBI
            isNew
            skuId
            isBest
            isNatural
            isOrganic
            imageAltText
            isOnlineOnly
            isFirstAccess
            isAppExclusive
            isLimitedEdition
            isLimitedTimeOffer
            isSephoraExclusive
            variationValue
            skuImages {
              image135
              image250
              image450
              __typename
            }
            sizeRefinement
            __typename
          }
          variationId
          facets {
            name
            values
            __typename
          }
          __typename
        }
        variationsMap {
          minListPrice
          maxListPrice
          minSalePrice
          maxSalePrice
          __typename
        }
        __typename
      }
      totalNumResults
      __typename
    }
    __typename
  }
}

query Refinements($locale: String) {
  refinements(locale: $locale) {
    key
    value
    categoryId
    refinements {
      key
      value
      items {
        key
        value
        visableInBP
        filterable
        image
        __typename
      }
      __typename
    }
    __typename
  }
}
