function AdvancedMatching(fireTagFx) {
  // Advance Matching
  var dd_user = digitalData.user ? digitalData.user[0] : null;
  var dd_profile = dd_user ? (dd_user.profile ? dd_user.profile[0] : null) : null;
  var dd_profileInfo = dd_profile ? dd_profile.profileInfo : null;
  var dd_hashedEmail = dd_profileInfo ? dd_profileInfo.email : null; // webAnalyticsHash
  var dd_profileId = dd_profileInfo ? dd_profileInfo.profileID || '' : null;

  var userInfoCache = Sephora.Util.getUserInfoCache();
  var phoneNumber = userInfoCache ? userInfoCache.profile.phoneNumber : null;

  Sephora.analytics.utils.hashString(dd_profileId).then(hashedProfileID => {
    ttq.identify({
      email: dd_hashedEmail, 
      // phone_number: phoneNumber,
      external_id: hashedProfileID,
    });

    fireTagFx();
  });
}

function FireTag_TikTokViewContent() {
  var eventId = 'viewcontent' + _satellite.getVar('[UEID] :: Universal Event ID Fx')(event);
  var currency = 'USD';
  var productId = '';
  var skuId = '';

  var price = '0.00';

  if (digitalData.product[0] && digitalData.product[0].attributes && digitalData.product[0].attributes.price !== undefined) {
    price = Number(digitalData.product[0].attributes.price.replace("$", "").replace(',', '.'));
    price = Sephora.analytics.utils.convertToUSD(price);
  }

  if (digitalData.product[0] && digitalData.product[0].productInfo && digitalData.product[0].productInfo?.productID !== undefined) {
    productId = digitalData.product[0].productInfo.productID;
  }

  if (digitalData.product[0] && digitalData.product[0].attributes && digitalData.product[0].attributes?.skuId !== undefined) {
    skuId = digitalData.product[0].attributes.skuId;
  }

  var content_name = (digitalData.product[0] && digitalData.product[0].productInfo.productName) || '';
  var category = (digitalData.product[0] && digitalData.product[0].attributes.nthLevelCategory) || '';
  var brand_name = (digitalData.product[0] && digitalData.product[0].productInfo.manufacturer) || '';
  var value = Number(price * 1);

  ttq.instance(_satellite.getVar('[TMS] TikTok_AccountID')).track('ViewContent', {
      quantity: 1,
      price: price,
      value: value,
      currency: currency,
      content_id: skuId,
      content_name: content_name,
      content_type: 'product',
      brand: brand_name,
      content_category: category
  }, {
      event_id: eventId
  });

}



if (typeof ttq === 'undefined') {
  // Expects the Tiktok Pixel to be loaded
  window.addEventListener('TikTokBasePixelInitialized', AdvancedMatching(FireTag_TikTokViewContent));
}
else {
  AdvancedMatching(FireTag_TikTokViewContent);
}","language":"javascript"}
