var gaId = _satellite.getVar('[TMS] UA Property Id');
var safelyConvertToUSD = function(price) {
    /* Remove any currency from the front or back of the string */
    price = price.match(/\d+(?:\.\d+|\,\d+\.\d+|\,\d+)|\d+/)[0]; /* Remove any commas because they break the convertToUSD method */ /* Adding decimals in place of commas for CA/FR */
    if (digitalData.page.pageInfo.language.toLowerCase() === 'fr') {
        price = price.replace(',', '.');
    } else {
        price = price.replace(',', '');
    } /* Convert to USD using Signal Library */
    //price = Sephora.analytics.utils.convertToUSD(price);
    return price;
};

var itemList = digitalData.cart.item;
var items = [];

itemList.forEach((item) => {
    let itemObj = {};
    itemObj.id = item.sku.skuId;
    itemObj.name = item.sku.productName + ' -> AL';
    itemObj.brand = item.sku.brandName || '';
    itemObj.category = item.sku.parentCategory && item.sku.parentCategory.displayName || '';
    itemObj.variant = item.sku.variationValue || '';
    itemObj.quantity = item.qty;
    let price = 0;
    let itemPriceToSend = (item.sku.type === 'Standard' && !item.sku.biType) || item.sku.type === 'Flash SKU' || item.sku.type === 'Gift Card';
    if (itemPriceToSend) {
        price = safelyConvertToUSD(item.sku.salePrice || item.sku.listPrice);
    }
    itemObj.price = Number(price).toFixed(2);
    items.push(itemObj);
});

gtag('event', 'begin_checkout', {
    'send_to': gaId,
    'items': items
});

