{"source":"    if(Sephora.template === 'Checkout/RwdCheckout' || Sephora.template === 'Checkout/Checkout') {
      const sti = Sephora.Util.cookieStore()?.sti || '';

      let biAccountID = '';
      const biAccountIDCache = localStorage.getItem('biAccountId');

      if (biAccountIDCache) {
          biAccountID = JSON.parse(biAccountIDCache)?.data;
      }

      const apiKeysMap = {
        ['us_en']: Sephora.configurationSettings.sessionAIApiKeyUS,
        ['ca_en']: Sephora.configurationSettings.sessionAIApiKeyCAEN,
        ['ca_fr']: Sephora.configurationSettings.sessionAIApiKeyCAFR
      }

      const cookies = Sephora.Util.cookieStore() || {};
      const { site_locale = '', site_language = '' } = cookies;

      const sessionAIApiKey = apiKeysMap[`${site_locale}_${site_language}`];

      fetch(_satellite.getVar("Session AI: events url"), {
          headers: {
              apikey: sessionAIApiKey
          },
          body: JSON.stringify({
              event: 'add_payment_info',
              logged_in: digitalData.user[0].segment.signInStatus === 'signed in' ? 'Y' : 'N',
              visitorId: sti,
              customerId: biAccountID,
              userAgent: navigator.userAgent,
              cart: JSON.stringify(
                  digitalData.cart.item.map(item => ({
                      product_id: item.sku.productId,
                      sku: item.sku.skuId,
                      quantity: item.qty,
                      price: Number(item.sku.valuePrice.replace('$', '')),
                      listPrice: Number(item.sku.listPrice.replace('$', '')),
                      category: item.sku.parentCategory.displayName
                  }))
              )
          }),
          method: 'POST'
      })
    }","language":"javascript"}
