function getPSTTime() {
  // Get current time in UTC
  let currentUTC = new Date();

  // Offset for PST in milliseconds (UTC -8 hours)
  let offset = -8 * 60 * 60 * 1000;

  // Apply the offset to get PST time
  let pstTime = new Date(currentUTC.getTime() + offset);

  const options = {
    year: 'numeric',
    month: 'numeric',
    day: 'numeric',
  };

  let pstTimePart = pstTime.toLocaleTimeString('en-US');
  let pstDatePart = pstTime.toLocaleDateString('en-US', options);
  return `${pstDatePart} ${pstTimePart}`;
}

function splitStringIntoChunks(str, chunkSize) {
    const chunks = [];
    for (let i = 0; i < str.length; i += chunkSize) {
        chunks.push(str.slice(i, i + chunkSize));
    }
    return chunks;
}

