{"source":"const eventId = `purchase-${_satellite.getVar("[UEID] :: Universal Event ID Fx")(event)}`;
const transactionId = digitalData.transaction.transactionID || null;
const sddSkuIds = digitalData.transaction.attributes.sdd.skuIds;

function FormatPrice(item) {
	let price ='0.00';
	if (item.sku.listPrice !== 'FREE') {
		price = item.sku.listPrice;
		price = price.replace(/\s+/g, "");
		price = price.match(/\d+(?:\.\d+|\,\d+\.\d+|\,\d+)|\d+/)[0];
		if (digitalData.page.pageInfo.language.toLowerCase() === 'fr') { 
			price = price.replace(',', '.'); 
		} 
		else { 
			price = price.replace(',', ''); 
		}
		price = Sephora.analytics.utils.convertToUSD(price);
	}
	return price;
}

function GetFormattedItems(items) {
	const item_skus = items.map(p => p.sku.skuId) || [];
	const item_qty = items.map(p => p.qty).join(',') || '';
	const item_categories = items.map(p => p.sku.parentCategory.displayName) || [];
	const item_prices = items.map(p => FormatPrice(p)).join(',');
    const item_brands = items.map(p => p.sku.brandName) || [];
    let totalPrice = item_prices.split(',').reduce(function (result, item) {
      return result + Number(item);
    }, 0);
    totalPrice = Math.round(totalPrice*100)/100;
	return { item_skus, item_qty, item_categories, item_prices, item_brands, totalPrice }
}

// Standard Delivery
const stdItems = digitalData.transaction.item.filter(p => 
	p.sku.type === 'Standard' && 
	p.sku.listPrice.indexOf('Points') <= -1 &&
	sddSkuIds.indexOf(p.sku.skuId) == -1);

// Same Day Delivery
const sddItems = digitalData.transaction.item.filter(p => sddSkuIds.indexOf(p.sku.skuId) >= 0);

if (clq) {

  // Standard Delivery
  if (stdItems.length > 0) {
    const { item_skus, totalPrice } = GetFormattedItems(stdItems);

    clq(
      'track', 
      'Purchase',{ 
        // eventId: eventId,
        value: totalPrice, 
        currency: 'USD', 
        content_type: 'product', 
        content_ids: item_skus, 
        order_id: transactionId, 
        subType: 'standard'
      }
    );
  }

  // SDD Order
  if (sddItems.length > 0) {
	const { item_skus, totalPrice } = GetFormattedItems(sddItems);

	clq(
      'track', 
      'Purchase',{ 
        // eventId: eventId,
        value: totalPrice, 
        currency: 'USD', 
        content_type: 'product', 
        content_ids: item_skus, 
        order_id: transactionId + '-sdd', 
        subType: 'sdd'
      }
    );
  }
}","language":"javascript"}
