{"source":"function getEventType() {
  let eventType = event?.nativeEvent?.type || '';
  let pageDetail = event?.detail?.data?.pageDetail || '';
  let linkName = event?.detail?.data?.linkName || '';
  let eventStrings = event?.detail?.data?.eventStrings || '';
  let productStrings = event?.detail?.data?.productStrings || '';
  let answerQuestionId = event?.detail?.data?.sponsoredProductInformation?.answerQuestionId || '';

  let contentType = '';
  let contentId = '';

  switch (eventType) {
    case 'asyncPageLoad':
      // Add Review
      if (eventStrings.indexOf('event149') >= 0) {
        contentType = 'product_review';
        contentId = _satellite.getVar('[TMS] Parse productString Fx')(productStrings)[0]?.product || '';
      }

      break;
    case 'linkTrackingEvent':
      // Answer Question
      if (linkName === 'answer_question_body_details') {
        contentType = 'product_question';
        contentId = answerQuestionId;
      }
      // Quiz Selected
      if (pageDetail === 'start-quiz') {
        contentType = 'quiz';
        contentId = event?.detail?.data?.bccComponentName || '';
      }
      break;

    case 'selectContentEvent':
      // Gallery Photo Upload
      if (linkName === 'photo_upload') {
        contentType = 'ugc_photo';
        contentId = event?.detail?.data?.photoId || '';
      }
      break;

    default:
      contentType = '';
      contentId = '';
  }

  return {
    contentType,
    contentId
  }
}

if (gtag) {
  const { contentType, contentId } =  getEventType();

  gtag ("event", 
    "select_content", 
    {
      content_type: contentType, 
      content_id: contentId
    }
  ); 
}","language":"javascript"}
