{"source":"const expirationInDays = 60;
const expirationTime = new Date().getTime() + expirationInDays * 86400000;
const utmParametersKey = 'utmParameters';

function getUtmParameters() {
  var utmParametersObject = {
      expiration: expirationTime
  };
  const currentTime = new Date().getTime();

  // Pull previously stored parameters
  var storedParameters = JSON.parse(localStorage.getItem(utmParametersKey));

  if (storedParameters !== undefined && storedParameters !== null) {
    if (storedParameters.expiration) {
      if (currentTime > storedParameters.expiration) {
        localStorage.removeItem(utmParametersKey); 
      }
      else {
        utmParametersObject = storedParameters;
      }
    }
  }

  return utmParametersObject;
}

function setUtmParameters(currentParameters) {
  // Get the current URL
  const url = new URL(window.location.href);

  // Get the query string parameters
  const params = new URLSearchParams(url.search);

  var changed = false;

  var utmParameters = ['utm_id', 'utm_source', 'utm_medium', 'utm_campaign', 'utm_source_platform', 'utm_term', 'utm_content', 'utm_creative_format', 'utm_marketing_tactic'];

  // Checks if parameter exists and stores it into the local storage
  utmParameters.forEach(u => {
      const utmParam = params.get(u); 
      if (utmParam !== undefined && utmParam !== null) {
        currentParameters[u] = utmParam;
        changed = true;
      }
  });

  if (changed) {
    currentParameters.expiration = expirationTime;
  }

  return currentParameters;
}

const newParameters = setUtmParameters(getUtmParameters());
localStorage.setItem(utmParametersKey, JSON.stringify(newParameters));




