{"source":"var tid = digitalData.transaction.transactionID; 
var universalEventId = _satellite.getVar('[UEID] :: Universal Event ID Fx')(event);
var eventId = `${universalEventId}`;

function FormatPrice(item) {
	let price ='0.00';
	if (item.sku.listPrice !== 'FREE') {
		price = item.sku.listPrice;
		price = price.replace(/\s+/g, "");
		price = price.match(/\d+(?:\.\d+|\,\d+\.\d+|\,\d+)|\d+/)[0];
		if (digitalData.page.pageInfo.language.toLowerCase() === 'fr') { 
			price = price.replace(',', '.'); 
		} 
		else { 
			price = price.replace(',', ''); 
		}
		price = Sephora.analytics.utils.convertToUSD(price);
	}
	return price;
}

function GetFormattedItems(items) {
	const item_skus = items.map(p => p.sku.skuId) || [];
	const item_qty = items.map(p => p.qty).join(',') || '';
	const item_categories = items.map(p => p.sku.parentCategory.displayName) || [];
	const item_prices = items.map(p => FormatPrice(p)).join(',');
    const item_brands = items.map(p => p.sku.brandName) || [];
	return { item_skus, item_qty, item_categories, item_prices, item_brands }
}

const bopisBasket = digitalData.transaction.item;
var bopisItems = [];
var countOfItemsInBasket = 0;
if (Array.isArray(bopisBasket) && bopisBasket.length > 0) {
	countOfItemsInBasket = bopisBasket.length;
	bopisItems = bopisBasket;
}

const { item_skus, item_qty, item_categories, item_prices, item_brands } = GetFormattedItems(bopisItems);

var dataToSend = {
  'currency': 'USD',
  'price': item_prices,
  'transaction_id': tid,
  'number_items': item_qty,
  'client_dedup_id': 'bopis-' + eventId,
  'item_ids': item_skus,
  'item_category': item_categories,
  'brands': item_brands
}

snaptr('track', 'PURCHASE', dataToSend);","language":"javascript"}
