{"source":"function getHighestLevelCategory(currentLevel) {
  return currentLevel?.parentCategory === undefined ? { categoryId: currentLevel.categoryId, displayName: currentLevel.displayName } : getHighestLevelCategory(currentLevel.parentCategory);
}

function ConvertAndFormat(valueToFormat) {
  // Formats the Price and Converts to USD when applicable.
  var om_value = valueToFormat || '';  
  om_value = om_value.replace(',', '.');
  om_value = om_value.replace('$', '');
  om_value = om_value.trim();
  om_value = Sephora.analytics.utils.convertToUSD(om_value);
}

if (optimoveSDK) {  
  var profileId = digitalData?.user[0]?.profile[0]?.profileInfo?.profileID || '';
  var biAccountId = Sephora.Util.getUserInfoCache()?.profile?.beautyInsiderAccount?.biAccountId || '';
  var eventData = event?.detail?.data || undefined;
  var orderId = digitalData.transaction?.transactionID || '';

  var items = digitalData?.transaction?.item || [];

  var om_departments = [];

  items.forEach(c_Item => om_departments.push(getHighestLevelCategory(c_Item?.sku?.parentCategory)?.displayName || ''));

  var om_skuIDs = items.map(i => i?.sku?.skuId || '');
  var om_productNames = items.map(i => i?.sku?.productName || '');
  var om_productBrands = items.map(i => i?.sku?.brandName || '');
  var om_productUrls = items.map(i => i?.sku?.url || '');
  var om_productIds = items.map(i => i?.sku?.productId || '');
  var om_categories = items.map(i => i?.sku?.parentCategory?.displayName || '');

  var om_qty = items.reduce((accumulator, i) => accumulator + (i?.qty || 0), 0);
  var om_totalPay = ConvertAndFormat(digitalData.transaction?.total?.transactionSubtotal || 0);
  var om_taxAmount = ConvertAndFormat(digitalData.transaction?.total?.tax || 0);
  var om_deliveryFee = ConvertAndFormat(digitalData.transaction?.total?.shipping || 0);

  var om_fulfillment_type = _satellite.getVar('[TMS] eVar133 - Shipping Method') || '';

  var parameters = {
    "userid": "",
    "biId": "",
    "eventdatetime": _satellite.getVar('[TMS] Event Datetime With Millisecond Format'),
    "order_id": "",
    "Source": "Optimove SDK",
    "currency": "USD",
    "sku_id": om_skuIDs,
    "item_name": om_productNames,
    "brand": om_productBrands,
    "category": om_categories,
    "department": om_departments,
    "url": om_productUrls,
    "product_id": om_productIds,
    "total_pay": om_totalPay,
    "tax_amount": om_taxAmount,
    "delivery_fee": om_deliveryFee,
    "quantity": om_qty,
    "fulfillment_type": om_fulfillment_type
  };

  Sephora.analytics.utils.hashString(profileId).then(hashedProfileId => {
    parameters = {
      ...parameters,
      "userid": hashedProfileId
    };
    Sephora.analytics.utils.hashString(biAccountId).then(hashedBiId => {
      parameters = {
        ...parameters,
        "biId": hashedBiId
      };
      Sephora.analytics.utils.hashString(orderId).then(hashedorderId => {
        parameters = {
          ...parameters,
          "order_id": hashedorderId
        }; 

        optimoveSDK.API.reportEvent('ootb_online_order', parameters);
      });
    });
  });
}","language":"javascript"}
