function AdvancedMatching(fireTagFx) {
  // Advance Matching
  var dd_user = digitalData.user ? digitalData.user[0] : null;
  var dd_profile = dd_user ? (dd_user.profile ? dd_user.profile[0] : null) : null;
  var dd_profileInfo = dd_profile ? dd_profile.profileInfo : null;
  var dd_hashedEmail = dd_profileInfo ? dd_profileInfo.email : null; // webAnalyticsHash
  var dd_profileId = dd_profileInfo ? dd_profileInfo.profileID || '' : null;

  var userInfoCache = Sephora.Util.getUserInfoCache();
  // var phoneNumber = userInfoCache ? userInfoCache.profile.phoneNumber : null;

  Sephora.analytics.utils.hashString(dd_profileId).then(hashedProfileID => {
    ttq.identify({
      email: dd_hashedEmail, 
      // phone_number: phoneNumber,
      external_id: hashedProfileID,
    });

    fireTagFx();
  });
}

function FireTag() {
    var eventId = 'addtocart' + _satellite.getVar('[UEID] :: Universal Event ID Fx')(event);

    var digitalDataPriceValue = event.detail.data.sku.listPrice;
    var price = Number(typeof digitalDataPriceValue === 'string' ? digitalDataPriceValue.replace("$", "").replace(",", ".")  : 0);
    price = String(Sephora.analytics.utils.convertToUSD(price));

    var isReward = (price.toLowerCase().indexOf('points') != -1);

    if ( (typeof price !== 'string') || (isReward || price === 'FREE') ) {
        price = 0;
    } 
    else {
        price = price.replace('$', '');

        if (digitalData.page.attributes.languageLocale === 'CA-FR') {
          price = Number(price.replace(',', '.'));
        } else {
          price = Number(price);
        }      
    }

    var quantity = Number(event === undefined ? '' : event.detail.data.analyticsData.qty);
    var content_name =  event.detail.data.analyticsData.productName || '';
    var brand_name = event.detail.data.analyticsData.brandName || '';
    var category = event.detail.data.analyticsData.category || '';
    var value = Number(price * quantity);

    ttq.instance(_satellite.getVar('[TMS] TikTok_AccountID')).track('AddToCart', {
    	quantity: quantity,
    	price: price,
    	value: value,
    	currency: event === undefined ? '' : event.detail.data.analyticsData.currency,
    	content_id: event === undefined ? '' : event.detail.data.analyticsData.sku.skuId,
    	content_name: content_name,
    	content_type: 'product',
        brand: brand_name,
        content_category: category
    }, {
    	event_id: eventId
    });
}

if (!ttq) {
  // Expects the Tiktok Pixel to be loaded
  window.addEventListener('TikTokBasePixelInitialized', AdvancedMatching(FireTag));
}
else {
  AdvancedMatching(FireTag);
}

