{"source":"if (optimoveSDK) {
  var eventData = event?.detail?.data || undefined;
  var biAccountId = Sephora.Util.getUserInfoCache()?.profile?.beautyInsiderAccount?.biAccountId || '';
  var profileId = digitalData?.user[0]?.profile[0]?.profileInfo?.profileID || '';

  // Formats the Price and Converts to USD when applicable.
  var om_price = eventData?.sku?.listPrice || '';  
  om_price = om_price.replace(',', '.');
  om_price = om_price.replace('$', '');
  om_price = om_price.trim();
  om_price = Sephora.analytics.utils.convertToUSD(om_price);

  var om_qty = eventData?.analyticsData.qty || 1;
  var om_totalprice = om_price * om_qty;

  var om_isBopis = eventData?.analyticsData?.isPickup || false;
  var om_isSdd = eventData?.analyticsData?.isSameDay || false;

  var fulfillment_type = 'standard_delivery';

  fulfillment_type = om_isBopis ? 'bopis' : fulfillment_type;
  fulfillment_type = om_isSdd ? 'sdd' : fulfillment_type;

  var om_category_id = eventData?.analyticsData?.sku?.parentCategory?.categoryId || '';
  var om_category_name = eventData?.analyticsData?.sku?.parentCategory?.displayName || '';

  var parameters = {
    "userid": "",
    "biId": "",
    "eventdatetime": _satellite.getVar('[TMS] Event Datetime With Millisecond Format'),
    "product_id": eventData?.analyticsData?.sku?.skuId || '',
    "sku_id": eventData?.analyticsData?.sku?.skuId || '',
    "brand": eventData?.analyticsData?.sku?.brandName || '',
    "category_id": om_category_id,
    "category_name": om_category_name,
    "department_id": om_category_id,
    "department_name": om_category_name,
    "page_title": eventData?.pageName || '',
    "currency": "USD",
    "Item_name": eventData?.analyticsData?.sku?.productName || '',
    "quantity": om_qty,
    "total_price": om_totalprice,
    "Source": "Optimove SDK",
    "fulfillment_type": fulfillment_type
    //"session_id": "",
    //"variant_id": "",
    //"url": "",
    //"page_category": "",
    //"tax_amount": "",
    //"delivery_fee": ""
  };

  Sephora.analytics.utils.hashString(profileId).then(hashedProfileId => {
    parameters = {
      ...parameters,
      "userid": hashedProfileId
    };
    Sephora.analytics.utils.hashString(biAccountId).then(hashedBiId => {
      parameters = {
        ...parameters,
        "biId": hashedBiId
      };

      optimoveSDK.API.reportEvent('ootb_add_to_cart', parameters);
    });
  });
}","language":"javascript"}
