{"global":false,"source":"function Pinteres_AddToCart() {

  let isRestrictedForPixel = _satellite.getVar('[TMS] IsRestrictedForPixel')(event);

  // AddToCart PIXELS should not fire for Gift Card, Samples, GWP & Rewards
  if (!isRestrictedForPixel) {
    let eventID = _satellite.getVar('[UEID] :: Universal Event ID Fx')(event);

    // Extracts the list price, removes the currency symbols and converts to USD when applicable.
    var listPrice = event.detail.data.sku.listPrice;
    listPrice = Sephora.analytics.utils.removeCurrencySymbol(listPrice);
    var cost = Sephora.analytics.utils.convertToUSD(listPrice); 

    var quantity = event === undefined ? '' : event.detail.data.analyticsData.qty;
    quantity = isNaN(Number(quantity)) ? quantity : Number(quantity);
    quantity = quantity.toString();

    var lineItems = [
      {
        product_name: event.detail.data.analyticsData.productName,
        product_id: event.detail.data.analyticsData.sku.skuId,
        product_price: cost,
        product_quantity: quantity
      }
    ];

    pintrk('track', 'AddToCart', {
        value: Math.round(cost * quantity * 100, 2) / 100,
        order_quantity: quantity,
        currency: 'USD',
        line_items: lineItems,
        event_id: 'addtocart-' + eventID
    });
  }
}

// Verifies the Pinterest pixel is loaded, if not, waits for to be initialized.
if (pintrk) {
  Pinteres_AddToCart();
}
else {
  Sephora.analytics.promises.PinterestBasePixelInitialized.then(function() {
    Pinteres_AddToCart()
  });
}","language":"javascript"}
