{"source":"function GetTimestamp() {
    try {
        var purchaseeventtimestamp =
           localStorage.getItem('purchaseeventtimestamp');
        if (!purchaseeventtimestamp) {
            return null;
        }
        else {
            var eventTimestamp = JSON.parse(purchaseeventtimestamp);
            return eventTimestamp.data || null;
        }
    }
    catch(e) {
        return null;
    }
} 
var timestamp = GetTimestamp();

timestamp = timestamp === null ? digitalData.page.attributes.date.timestamp : timestamp; 

var facebookPixelId = _satellite.getVar('[TMS] Facebook Pixel ID');
var biAccountId = Sephora.Util.getUserInfoCache()?.profile?.beautyInsiderAccount?.biAccountId || '';
var brand = digitalData.transaction.attributes.brandNames;
var product_type = digitalData.transaction.attributes.categories;
var eventID = "purchase-" + _satellite.getVar('[UEID] :: Universal Event ID Fx')(event);

var itemShorts = digitalData.transaction.itemShort;
var isRestrictedForPixel = digitalData.cart.isRestrictedForPixel;

// Total without Taxes.
var value = Number(digitalData.transaction.total.transactionSubtotal);

var content = [];

for (var i = 0; i < itemShorts.length; i++) {
    var itemShort = itemShorts[i];
    var product = {
        'id': itemShort.id,
        'quantity':itemShort.quantity,
        'brand': brand[i],
        'product_type': product_type[i],
        'item_price': Number(itemShort.price),
    };
    content.push(product);
}

// AQME-4645: Discrepancy between AL and SOT. Tags should send CAD when using Canadian site
var currency = digitalData.page.pageInfo.country === 'US' ? 'USD' : 'CAD';

var details = {
    order_id: _satellite.getVar("[TMS] Page :: Order Number"),
    payment_method: _satellite.getVar("[TMS] Facebook :: Order :: Payment Method"),
    delivery_option: _satellite.getVar("[TMS] Page :: Order :: Delivery Option"),
    content_local: _satellite.getVar("[TMS] Page :: Language/Locale"),
    customer_type: _satellite.getVar("[TMS] Pixel :: Facebook :: BI Status")
};
var data = {
    contents: content,
    value: value,
    details: details,
    content_type: 'product',
    currency: currency
}

if(!isRestrictedForPixel){
  if (biAccountId) {
    Sephora.analytics.utils.hashString(biAccountId).then(hashedBiId => {
      fbq(
        'trackSingle',
        facebookPixelId,
        'Purchase',
        {
          ...data,
          external_id: hashedBiId
        },
        eventID
      );
    });
  } else {
      fbq('trackSingle', facebookPixelId, 'Purchase', data, {
          eventID: eventID
      });
  }
}

var fulfillment_type = _satellite.getVar('[TMS] eVar133 - Shipping Method') || '';

// Processes the BOPIS order. This is so both events have the same Event ID
if (fulfillment_type.indexOf('pickup') >= 0) {
  var contents = [];
  var skuIds = digitalData.transaction.attributes.skuIds;
  var skuQty = digitalData.transaction.attributes.itemQty;

  if (skuIds.length) {
      for (var i = 0; i < skuIds.length; i++) {
          var item = {};
          item.id = skuIds[i];
          item.quantity = skuQty[i];
          contents.push(item);
      }
  }

  // AQME-4645: Discrepancy between AL and SOT. Tags should send CAD when using Canadian site
  var currency = digitalData.page.pageInfo.country === 'US' ? 'USD' : 'CAD';

  // Transaction Total without taxes.
  var transactionSubTotal = digitalData.transaction.total.transactionSubtotal || '0';

  var cleanedTransactionSubTotal = transactionSubTotal.replace("$", "").replace(',', '.');
  var value = Number(cleanedTransactionSubTotal).toFixed(2); 

  var eventId = "bopis-" + _satellite.getVar('[UEID] :: Universal Event ID Fx')(event);

  var data = {
      contents: contents,
      content_type: 'product',
      value: value,
      currency: currency,
      customer_type: _satellite.getVar("[TMS] Pixel :: Facebook :: BI Status"),
      content_local: _satellite.getVar("[TMS] Page :: Language/Locale"),
      order_id: _satellite.getVar("[TMS] Page :: Order Number")
  };

  if (biAccountId) {
    Sephora.analytics.utils.hashString(biAccountId).then(hashedBiId => {
      fbq('trackCustom', 'bopisReservation', { ...data, external_id: hashedBiId }, { eventID: eventId });
    });
  } else {
    fbq('trackCustom', 'bopisReservation', data, { eventID: eventId });
  }
}

